from bs4 import BeautifulSoup
import os
from lxml import etree
from lxml import html
import re


def extract_suites(email_body):
    # Open the HTML file
    current_directory = os.path.dirname(os.path.abspath(__file__))
    html_file_path = os.path.join(current_directory, 'email.EmailWebScrapping')
    html_content = ""
    current_directory = os.path.dirname(os.path.abspath(__file__))

    file_path = os.path.join(current_directory, 'email.EmailWebScrapping')

    with open(file_path, 'w', encoding="utf-8") as file:
        file.write(str(email_body))
    print("<<<<>>>")
    with open(file_path, "r", encoding="utf-8") as file:
        html_content = file.read()

    soup = BeautifulSoup(html_content, "html.parser")
    print('Soup--->')

    soup = BeautifulSoup(html_content, 'html.parser')
    dom = etree.HTML(str(soup))
    departure_city = dom.xpath('//table[2]//td//span')
    arrival_city = dom.xpath('//table[4]//td//span')
    price_data = dom.xpath('//table[5]//td//span')

    departure_city1 = list_to_dict(departure_city, 'departure_city')
    arrival_city1 = list_to_dict(arrival_city, 'arrival_city')
    price1 = price_list_to_dict(price_data, 'fare')
    return departure_city1, arrival_city1, price1


def list_to_dict(extract_city, key_name):
    my_dict = {}
    length = len(extract_city)
    print(length)
    j = 1
    for i in range(length):
        if i % 2 == 0:
            continue
        else:
            key = key_name + str(j)
            city = extract_city[i].text
            # print(city)
            city = city.rstrip()
            my_dict[key] = city
            j = j + 1

    return my_dict


def price_list_to_dict(price, key_name):

    length = len(price)
    print(length)
    j = 1
    list = []
    for i in range(length):
        key = key_name + str(j)
        price2 = price[i].text
        # print(price2)
        list.append(price2)

    my_dict = convert_list(list, "fare")
    return my_dict


def convert_list(prices, key_name):
    my_dict = {}
    j = 1
    price_pattern = re.compile(r'₹\s*\d{1,3}(,\d{2,3})*(,\d{3})*\.\d{2}')
    for price in prices:
        match = re.search(price_pattern, price)
        if match:
            key = f"{key_name}{j}"
            # Remove unwanted characters and whitespace
            cleaned_price = match.group().replace('₹\xa0', '').strip()
            cleaned_price = float(cleaned_price.replace(',', ''))
            # cleaned_price = round(cleaned_price - 0.01, 2)
            my_dict[key] = cleaned_price
            j += 1
    return my_dict

